pub fn install() {
    Agent::new("falco")
    .acmd("game_attacks3", game_daisy_attacks3, Priority::Low)
    .acmd("game_attackhi3", game_daisy_attacklw3, Priority::Low)
    .acmd("game_attacklw3", game_daisy_attacklw3, Priority::Low)
    .install();
}