pub fn install() {
    Agent::new("marth")
    .acmd("game_attacks3", game_marth_attacks3, Priority::Low)
    .acmd("game_attackhi3", game_marth_attackhi3, Priority::Low)
    .acmd("game_attacklw3", game_marth_attacklw3, Priority::Low)
    .install();
}